/* * FixedPoint.cpp * * DESCRIPTION: Returns the fixed point of an arbitrary nonlinear function using *              general fixed point iteration * * Created on: November 26, 2016 * * AUTHORS: * Jaquier, Michael <michael.jaquier@epfl.ch> * Lorkowski, Alexander <alexander.lorkowski@epfl.ch> */#include "FixedPoint.hpp"FixedPoint::FixedPoint(Expression &equation,					   double initial,					   double tolerance,					   int maxIter,					   bool verbosity)		: NonlinearSolver(equation, initial, tolerance, maxIter, verbosity){}FixedPoint::~FixedPoint() {}double FixedPoint::solve() {    double x1 = 0, error_est;    int i;    if (verbose) { printVerbose(0, x0); }    for (i = 1; i <= nMax; i++) {    	x1 = eq.evaluate(x0);    	if (verbose) { printVerbose(i, x1); }    	error_est = fabs(x1 - x0);        if ( error_est < tol ) {            return x1;        } else {            x0 = x1;        }    }    std::cout << "Maximum number of iterations exceeded" << std::endl;    return x1;}